<script lang="ts">
    import { onDestroy, onMount } from "svelte";
    import { fly } from "svelte/transition";

    interface Props {
        size: number;
    }

    let { size }: Props = $props();

    let logo = $state() as HTMLElement;
    let styleLeft = $state("transform: rotate(-79.186deg);");
    let styleRight = $state("transform: rotate(79.186deg);");

    let Document: any;

    function animate(evt: MouseEvent | Touch) {
        const x = evt.clientX;
        const y = evt.clientY;

        const rect = logo.getBoundingClientRect();
        const anchorX = rect.left + rect.width / 2;
        const anchorY = rect.top + rect.height / 2;

        const angleDeg = 90 + angle(x, y, anchorX, anchorY);
        styleLeft = `transform: rotate(${angleDeg}deg);`;
        styleRight = `transform: rotate(${angleDeg}deg);`;
    }

    function angle(cx: number, cy: number, ex: number, ey: number) {
        const dy = ey - cy;
        const dx = ex - cx;
        const rad = Math.atan2(dy, dx);
        const deg = (rad * 180) / Math.PI;
        return deg;
    }

    onMount(()=>{

        Document = window.document;

        document.ontouchmove = (evt) => {
            animate(evt.touches[0]);
        };
        document.onmousemove = (evt) => {
            animate(evt);
        };
    });

    onDestroy(()=>{
        if (Document){
            Document.ontouchmove = null;
            Document.onmousemove = null;
        }
    });

</script>

<div id="logo" style="height: {size}px; width: {size}px;" bind:this={logo} transition:fly={{y: -20}}>
    <img
        src="data:image/png;base64,"
        height="120px"
        width="120px"
        alt="logo"
    />
    <div class="eyesocket left" style="{styleLeft}">
        <div class="eye"></div>
    </div>
    <div class="eyesocket right" style="{styleRight}">
        <div class="eye"></div>
    </div>
</div>

<style lang="scss">
    #logo {
        position: relative;
        img{
            height: 100%;
            width: 100%;
        }
    }

    .eyesocket {
        position: absolute;
        top: 47%;
        height: 15%;
        width: 15%;
        border-radius: 50%;
        background: #33363a;
    }

    .eyesocket.left {
        left: 24%;
    }

    .eyesocket.right {
        right: 24%;
    }

    .eye {
        height: 18%;
        width: 18%;
        top: 62%;
        border-radius: 50%;
        left: 50%;
        transform: translateX(-50%);
        background: #e9e9e9;
        position: absolute;
    }
</style>
